mapper.select:
select * from (
	select rownum rnum, A.* from(
		select * from member
		order by member_no desc
	)A
) member
where rnum between #{startNo} and #{endNo}

choose.when:
	select * from (
         select rownum rnum, B.* from(
            select * from member
            order by member_no desc
         )B
      ) member
      where rnum between #{paging.startNo} and #{paging.endNo}

select * from (
   select rownum rnum, B.* from(
      select * from member
      where grade =#{grade}
      order by member_no desc
   )B
) member
where rnum between #{paging.startNo} and #{paging.endNo}

mapper.select:

select * from (
     select rownum rnum, B.* from(
         select * from member
         where ${category} like '%'||#{content}||'%'
          order by member_no desc
        )B
     ) member
where rnum between #{paging.startNo} and #{paging.endNo}

select count(*) from project
where project_step in(3,4)

	and project_step = #{step}


	and category_no = #{categoryNo}


select * from (
     select rownum rnum, N.* from(
         select * from notice
          order by notice_no desc
        )N
     ) notice
where rnum between #{startNo} and #{endNo}

mapper.delete:
	delete from noticefile
	where notice_no = #{notice.noticeNo}
	and file_no not in

     #{item}

mapper.select:
    	select * from (
		     select rownum rnum, N.* from(
		         select * from project 
		      		where project_step between 3 and 4

						and project_step = #{step}


						and category_no = #{categoryNo}

		          order by project_no desc
		        )N
		     ) project
		where rnum between #{paging.startNo} and #{paging.endNo}

select * from (
     select rownum rnum, N.* from(
         select * from project 
      		where project_step =1
          order by project_no desc
        )N
     ) project
where rnum between #{startNo} and #{endNo}

