mapper.select:
SELECT budget_plan, schedule_plan
FROM project
WHERE project_no = #{projectNo}

SELECT news_no, news_title, news_date
FROM project_news
WHERE project_no = #{projectNo}

SELECT comment_no, comment_content, comment_writer, comment_date
FROM project_comment
WHERE project_no = #{projectNo}

SELECT news_title, news_content, news_date
FROM project_news
WHERE news_no = #{newsNo}
