mapper.select:
SELECT member_no, id, pw, name, email, nick, phone, grade 
FROM member
WHERE id = #{loginid}

mapper.update:
UPDATE member
SET name = #{name}, nick = #{nick}, pw = #{pw}, email = #{email}, phone = #{phone}
WHERE member_no = #{memberNo}

mapper.select:
SELECT count(*) cnt
FROM member
WHERE nick = #{nick}

SELECT member_no, id, pw, name, email, nick, phone, grade 
FROM member
WHERE id = #{id}

SELECT count(*) cnt
FROM member
where id = #{id} and pw = #{pw}

SELECT member_no, bank_name, account_number, account_name, seller_info
FROM member_seller
WHERE member_no = #{loginNo}

mapper.update:
UPDATE member_seller
SET bank_name = #{bankName}, account_number = #{accountNumber} , account_name = #{accountName}, seller_info = #{sellerInfo}
WHERE member_no = #{memberNo}

mapper.select:
SELECT * FROM ( SELECT rownum rnum, list.* FROM ( select DISTINCT o.order_no, o.order_date, p.project_title, p.project_no, p.project_step, o.order_status, c.category_name from reward r, reward_order ro, order_tb o, project p, category c where p.project_no = r.project_no and r.reward_no = ro.reward_no and ro.order_no = o.order_no and c.category_no = p.category_no
and o.member_no = #{memberNo}
AND project_step LIKE '%'|| #{search} ||'%'
AND project_title LIKE '%'|| #{search} ||'%'
order by o.order_date desc ) list ) R WHERE rnum between #{startNo } and #{endNo }

SELECT count(*) cnt FROM project where 1=1 and member_no = #{memberNo}
AND project_step LIKE '%'|| #{search} ||'%'
AND project_title LIKE '%'|| #{search} ||'%'

SELECT * FROM ( SELECT rownum rnum, list.* FROM ( select p.PROJECT_NO, p.MEMBER_NO, p.CATEGORY_NO, p.PROJECT_TITLE , p.PROJECT_INTRO, p.BUDGET_PLAN ,p.SCHEDULE_PLAN ,p.PROJECT_IMAGE , p.PROJECT_PRICE, p.OPEN_DATE, p.CLOSE_DATE ,p.DELIVERY_DATE ,p.PROJECT_CONTENT ,p.FUND_PRICE ,p.PROJECT_STEP , c.category_name from project p, category c where c.category_no = p.category_no
and p.member_no = ${memberNo}
AND project_step LIKE '%'|| #{search} ||'%'
AND project_title LIKE '%'|| #{search} ||'%'
order by p.project_no desc ) list ) R WHERE rnum between #{startNo } and #{endNo }

select REWARD_NO, PROJECT_NO,REWARD_PRICE,REWARD_AMOUNT,REWARD_NAME,REWARD_INTRO,REWARD_STATE
from reward r
where project_no = #{projectNo}

select count(*)
from qna
where member_no = #{memberNo}

SELECT * FROM (
    SELECT rownum rnum, list.* FROM (
        select qna_title, qna_create_date, qna_no, member_no, qna_refer
        from qna
        where 
            member_no = ${memberNo}
        order by qna_no desc
    ) list
) R
WHERE rnum BETWEEN 1 AND 5
